	public void saveFileAdChanged(HashMap<UUID, Advertisement> advers, String contextPath) {
		try {
			ObjectMapper objectMapper = new ObjectMapper();
			objectMapper.configure(DeserializationFeature.ACCEPT_SINGLE_VALUE_AS_ARRAY, true);
			objectMapper.configure(DeserializationFeature.ACCEPT_EMPTY_STRING_AS_NULL_OBJECT, true);
			objectMapper.configure(DeserializationFeature.ACCEPT_EMPTY_ARRAY_AS_NULL_OBJECT, true);
			
			ArrayList<Advertisement> proba = new ArrayList<>();
			
			for(Advertisement a : advers.values()) {
				proba.add(a);
			}
			objectMapper.writeValue(new File(contextPath+"/ads.json"), proba);
			
			System.out.println(advers + "U file upisi");
		}
		catch(Exception ex) {
			System.out.println(ex);
			ex.printStackTrace();
		}
		finally {
			
		}
	}
	
	
	
	
	
	
	
	
	
	
	
	
	
	
	
	
	
	
	
	
	
	
	
	
	
	
	
	
	
	
